<script>
import Request from '@/services/Request.js'
export default {
  data(){
    return {
      productId: null,
      productPrice: null,
      productName: null,
      products: [
        {
          id: 156,
          name: 'Perfecting Serum 30 Day Supply',
          price: '59.53'
        },
        {
          id: 157,
          name: 'Perfecting Serum 60 Day Supply',
          price: '77.72'
        },
        {
          id: 158,
          name: 'Perfecting Serum 90 Day Supply',
          price: '104.85'
        }
      ]
    }
  },
  methods: {
    thankYou(){
      this.$router.push('/thank-you');
    },
    upselling(e){
      this.$store.state.loader = true;

      localStorage.crossId = e.target.dataset.id;

      let data = {
        orderId: this.$store.state.orderId,
        product1_id: e.target.dataset.id,
        amount: e.target.dataset.price,
        emailAddress: localStorage.emailAddress
      }

      Request.upselling(data)
        .then(res => {
          this.$store.state.loader = false;
          if(res.result === "SUCCESS"){
            this.success();
          } else if(res.result === "ERROR"){
            if( typeof res.message === 'object' ){
              for (const [key, value] of Object.entries(res.message)) {
                alert(`Something goes wrong. Error! ${key}: ${value}`);
              }
            } else {
              alert(`Something goes wrong. Error! ${res.message}`);
            }
          } else {
            alert('Something goes wrong! Please reload the page and try again!');
          }
        });
    },
    success(){
      let id = localStorage.crossId;
      let product_price;
      this.products.forEach(product => {
        if(product.id == id){
          product_price = product.price;
        }
      })
      let crossData = {
        price: product_price,
        name: 'Deep perfecting serum'
      }

      localStorage.crosssell = JSON.stringify(crossData);

      fbq('track', 'PurchaseCrossSell', {
        currency: "USD",
        value: product_price,
        transaction_id: localStorage.orderId
      });
      dataLayer.push({
        'event': 'PurchaseCrossSell',
        'currency': "USD",
        'value': `${product_price}`,
        'transaction_id': `${localStorage.orderId}`
      });

      setTimeout(()=>{
        this.$router.push('/thank-you');
      }, 500);
    },
    confirmPP(){
      let success = () => this.success();
      let id = localStorage.crossId;
      let product_price;
      this.products.forEach(product => {
        if(product.id == id){
          product_price = product.price;
        }
      })

      this.$store.state.loader = true;
      Request.konnektivePaypalUpsaleConfirm({
        token: this.$route.query.token,
        payerId: this.$route.query.PayerID,
        sessionId: localStorage.sessionId,
        orderId: localStorage.orderId,
        product1_id: localStorage.crossId,
        campaignId: localStorage.campaignId,
        emailAddress: localStorage.emailAddress,
        amount: product_price
      })
        .then(res => {
          this.$store.state.loader = false;
          if( res.result == 'SUCCESS' ){
            success();
          }
        });
    },
    paypalUpsale(e){
      localStorage.paypalConfirmed = 0;
      this.$store.state.loader = true;
      localStorage.crossId = e.target.dataset.id;
      Request.konnektivePaypalUpsale({
        orderId: localStorage.orderId,
        product1_id: e.target.dataset.id,
        url: window.location.href
      })
        .then(res => {
          this.$store.state.loader = false;
          window.location.href = `${res.message.paypalUrl}`;
        });
    }
  },
  mounted(){
    window.scrollTo(0, 0);
    if( localStorage.orderId && localStorage.payment ){
      this.$store.state.orderId = localStorage.orderId;
      this.$store.state.payment = localStorage.payment;
      if( this.$route.query.paypalAccept == '1' && localStorage.paypalConfirmed == 0){
        localStorage.paypalConfirmed = 1;
        this.confirmPP();
      }else if(this.$route.query.paypalAccept == '1' && localStorage.paypalConfirmed == 1){
        this.success()
      }
    } else {
      if(this.$store.state.payment != 'cc'){
        this.$router.push('/');
      }
    }
  }
}
</script>
